import React, { useState } from 'react';
import './UserInput.css';

const initialUserInput = {
  'current-savings': 10000,
  'yearly-contribution': 1200,
  'expected-return': 7,
  duration: 10,
};

const UserInput = (props) => {
  const [enteredData, setEnteredData] = useState(initialUserInput);
  console.log(enteredData);

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(event);
  };

  const resetHandler = () => {
    setEnteredData(initialUserInput);
  };

  const inputChangeHandler = (input, value) => {
    setEnteredData((prevState) => {
      return { ...prevState, [input]: Number(value) };
    });
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <div className="input-group">
        <p>
          <label htmlFor={'current-savings'}>Current Savings ($)</label>
          <input
            type="number"
            id={'current-savings'}
            onChange={(event) =>
              inputChangeHandler('current-savings', event.target.value)
            }
            value={enteredData['current-savings']}
          />
        </p>
        <p>
          <label htmlFor={'yearly-contribution'}>Yearly Savings ($)</label>
          <input
            type="number"
            id={'yearly-contribution'}
            onChange={(event) =>
              inputChangeHandler('yearly-contribution', event.target.value)
            }
            value={enteredData['yearly-contribution']}
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor={'expected-return'}>
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id={'expected-return'}
            onChange={(event) =>
              inputChangeHandler('expected-return', event.target.value)
            }
            value={enteredData['expected-return']}
          />
        </p>
        <p>
          <label htmlFor={'duration'}>Investment Duration (years)</label>
          <input
            type="number"
            id={'duration'}
            onChange={(event) =>
              inputChangeHandler('duration', event.target.value)
            }
            value={enteredData['duration']}
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetHandler}>
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default UserInput;
