import React from 'react';
import './UserInput.css';
import Input from './Input';

const UserInput = (props) => {
  return (
    <form className="form" onSubmit={props.calculateHandler}>
      <div className="input-group">
        <Input category={'current-savings'}>Current Savings ($)</Input>
        <Input category={'yearly-contribution'}>Yearly Savings ($)</Input>
      </div>
      <div className="input-group">
        <Input category={'expected-return'}>Expected Interest (%, per year)</Input>
        <Input category={'duration'}>Investment Duration (years)</Input>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default UserInput;
